mapper.update:
update user_info
set user_name = #{userName,jdbcType=VARCHAR},
  sex = #{sex,jdbcType=CHAR},
  is_disabled = #{disabled,jdbcType=BIT}
where user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select user_id, user_name, sex, is_disabled
from user_info
where user_id = #{userId,jdbcType=INTEGER}
